import React, { useState } from "react";
import Swal from "sweetalert2";
import { getUserAndProject } from "../../hooks/getUserAndProject";

const Edit = ({ tasks, selectedTask, setTasks, setIsEditing }) => {
  const id = selectedTask.id;

  const [name, setName] = useState(selectedTask.name);
  const [desc, setDesc] = useState(selectedTask.desc);
  const [user, setUser] = useState(selectedTask.user);
  const [project, setProject] = useState(selectedTask.project);
  const [date, setDate] = useState(selectedTask.date);

  const { users, projects } = getUserAndProject();

  const handleUpdate = (e) => {
    e.preventDefault();

    if (!name || !desc || !user || !project || !date) {
      return Swal.fire({
        icon: "error",
        title: "Error!",
        text: "All fields are required.",
        showConfirmButton: true,
      });
    }

    const task = {
      id,
      name,
      desc,
      user,
      project,
      date,
    };

    // TODO: Update document

    setTasks(task);
    setIsEditing(false);

    Swal.fire({
      icon: "success",
      title: "Updated!",
      text: `Task has been updated.`,
      showConfirmButton: false,
      timer: 1500,
    });
  };

  return (
    <div className="small-container">
      <form onSubmit={handleUpdate}>
        <h1>Edit Task</h1>
        <label htmlFor="firstName">First Name</label>
        <input
          id="firstName"
          type="text"
          name="firstName"
          value={firstName}
          onChange={(e) => setFirstName(e.target.value)}
        />
        <label htmlFor="lastName">Last Name</label>
        <input
          id="lastName"
          type="text"
          name="lastName"
          value={lastName}
          onChange={(e) => setLastName(e.target.value)}
        />
        <label htmlFor="email">Email</label>
        <input
          id="email"
          type="email"
          name="email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
        />
        <label htmlFor="salary">Salary ($)</label>
        <input
          id="salary"
          type="number"
          name="salary"
          value={salary}
          onChange={(e) => setSalary(e.target.value)}
        />
        <label htmlFor="date">Date</label>
        <input
          id="date"
          type="date"
          name="date"
          value={date}
          onChange={(e) => setDate(e.target.value)}
        />
        <div style={{ marginTop: "30px" }}>
          <input type="submit" value="Update" />
          <input
            style={{ marginLeft: "12px" }}
            className="muted-button"
            type="button"
            value="Cancel"
            onClick={() => setIsEditing(false)}
          />
        </div>
      </form>
    </div>
  );
};

export default Edit;
